RULES_ITEM.SQL_TEXT:
begin
    for rec in (
        select xitor_id
          from users
         where xitor_id is not null
    ) loop
        user_management.user_flags(rec.xitor_id);
    end loop;
end;

begin
    for rec in (
        select xitor_id
          from users
         where xitor_id is not null
    ) loop
        user_management.set_user_class(rec.xitor_id);
    end loop;
end;

declare
    v_user_trackor_id xitor.xitor_id%type;
begin

    select xitor_id
      into v_user_trackor_id
      from users
     where user_id = :user_id;

    if v_user_trackor_id is null then
        return;
    end if;

    user_management.set_user_class(v_user_trackor_id);

end;

declare
    v_user_id users.user_id%type;
begin
    select user_id
      into v_user_id
      from users
     where xitor_id = :pk;
    user_management.set_disabled_enabled_created_date(v_user_id);
end;

declare
    v_user_xitor_id xitor.xitor_id%type;
begin

    select xitor_id 
      into v_user_xitor_id 
      from users 
     where user_id = :user_id;

    if v_user_xitor_id is null then 
        return; 
    end if;

    user_management.user_flags(v_user_xitor_id);

end;
